import React from "react";
import axios from 'axios'


import StripeCheckout from "react-stripe-checkout";

const StripeCheckoutButton = ({price}) => {

    const priceForStripe = price * 100
    const publishableKey = 'pk_test_51IUDqpL9zi6mNXtnehXcctyHUK9sRqmRQQeAokZkaLMPCUVv3hsefRBRR0w2aV0eTAT4aFZSoDukgtNIK8GV2Voh00lcuhfv5E' 
    const onToken = async token => {

    try {

        await axios({
            url:'payment',
            method:'post',
            data: {

                amount: priceForStripe,
                token
            }
        })

        alert('Payment succeeded!')
    } catch (error) {

        console.log('Payment Error: ', JSON.parse(error))
        alert('there was an issue with your payment. Please make sure to use the provided cart.')
    }

    }
    return (<StripeCheckout 
              label = 'Pay Now'
              name = 'CRWN Clothing Ltd.'
              shippingAddress
              billingAddress
              image = 'https://svgshare.com/i/CUz.svg'
              description ={`Your total is $${price}`}
              amount={priceForStripe}
              panelLabel = 'Pay Now'
              token = {onToken}
              stripeKey = {publishableKey}

    />)
}


export default StripeCheckoutButton
