import React, { useEffect, lazy, Suspense } from 'react';
import { Switch, Route, Redirect } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';

import Header from './components/header/header.component';
import Spinner from './components/spinner/spinner.component';
import ErrorBoundary from './components/error-boundary/error-boundary.component';

import { selectCurrentUser } from './redux/user/user.selectors';
import { checkUserSession } from './redux/user/user.actions';



import GlobalStyle from './global.styles';


const HomePage = lazy(()=>import('./pages/homepage/homepage.page'))
const ShopPage = lazy(()=>import('./pages/shop/shop.page'))
const SignInSignUpPage = lazy(()=>import('./pages/sign-in-sign-up/sign-in-sign-up.page'))
const Checkout = lazy(()=>import( './pages/checkout/checkout.page'))


const  App = () => {


  const currentUser = useSelector(selectCurrentUser)
  const dispatch = useDispatch()


 useEffect(() => checkUserSession(), [dispatch])



    return (
      <div>
        <GlobalStyle />
        <Header />
        <Switch>
          <Suspense fallback={<Spinner />}>
            <Route exact path='/' component={HomePage} />

          <Route path='/shop' component={ShopPage} />
          <Route path='/sign-in' render = {() => currentUser ? (<Redirect to='/' />) : (<SignInSignUpPage />) } />
          <Route path='/checkout' component ={Checkout} />
          </Suspense>
        </Switch>
      </div>
    );

}



export default App;
